fn test_cpfp_transaction_prioritization() {
    // Create a test UTXO set
    let mut utxo_set = UTXOSet::new();

    // Add UTXOs for all transactions first
    // Add a UTXO for the parent transaction
    utxo_set.add_utxo(
        OutPoint {
