        assert!(manager.add_peer(node2.clone(), ConnectionType::Outbound).is_ok());
        
        // Update reputation until banned
        for _ in 0..200 {
            manager.update_peer_score(&node2.addr, false);
        }
        assert!(manager.is_banned(&node2.addr));
    }

